import React, { useState } from "react";
import { ReactSortable } from "react-sortablejs";
import styled from "styled-components";
import "./styles.css";
import { classes } from './classes.js'

const StyledBlockWrapper = styled.div`
  position: relative;

  background: white;
  padding: 20px;
  margin-bottom: 10px;
  border: 1px solid lightgray;
  border-radius: 4px;
  cursor: move;
  &:hover {
    //background: #eeeeee;
  }
`;

const sortableOptions = {
  animation: 150,
  fallbackOnBody: true,
  swapThreshold: 0.65,
  ghostClass: "ghost",
  group: "shared"
};

export default function App() {
  const [blocks, setBlocks] = useState([
    {
      id: 1,
      content: "Team 1",
      parent_id: null,
      type: "container",
      children: [
        {
          id: 2,
          content: classes[0],
          width: 3,
          type: "text",
          parent_id: 1
        },
        {
          id: 3,
          content: classes[1],
          width: 3,
          type: "text",
          parent_id: 1
        },
        {
          id: 4,
          content: classes[2],
          width: 3,
          type: "text",
          parent_id: 1
        },
        {
          id: 5,
          content: classes[3],
          width: 3,
          type: "text",
          parent_id: 1
        },
        {
          id: 6,
          content: classes[4],
          width: 3,
          type: "text",
          parent_id: 1
        },
        {
          id: 7,
          content: classes[5],
          width: 3,
          type: "text",
          parent_id: 1
        },
      ]
    },

    {
      id: 8,
      content: "Team 2",
      parent_id: null,
      type: "container",
      children: [
        {
          id: 9,
          content:classes[0],
          width: 2,
          type: "text",
          parent_id: 8
        },
        {
          id: 10,
          content: classes[1],
          width: 2,
          type: "text",
          parent_id: 8
        },
        {
          id: 11,
          content: classes[2],
          width: 2,
          type: "text",
          parent_id: 8
        },
        {
          id: 12,
          content: classes[3],
          width: 3,
          type: "text",
          parent_id: 8
        },
        {
          id: 13,
          content: classes[4],
          width: 3,
          type: "text",
          parent_id: 8
        },
        {
          id: 14,
          content: classes[5],
          width: 3,
          type: "text",
          parent_id: 8
        },
      ]
    },
    {
      id: 15,
      content: "Team 3",
      parent_id: null,
      type: "container",
      children: [
        {
          id: 16,
          content: classes[0],
          width: 3,
          type: "text",
          parent_id: 15
        },
        {
          id: 17,
          content: classes[1],
          width: 3,
          type: "text",
          parent_id: 15
        },
        {
          id: 18,
          content: classes[2],
          width: 3,
          type: "text",
          parent_id: 15
        },
        {
          id: 19,
          content: classes[3],
          width: 3,
          type: "text",
          parent_id: 15
        },
        {
          id: 20,
          content: classes[4],
          width: 3,
          type: "text",
          parent_id: 15
        },
        {
          id: 21,
          content: classes[5],
          width: 3,
          type: "text",
          parent_id: 15
        },
      ]
    },
  ]);

  return (


      <ReactSortable list={blocks} setList={setBlocks} {...sortableOptions}>
        {blocks.map((block, blockIndex) => (
          <div className="grid-item">
          <BlockWrapper
            key={block.id}
            block={block}
            blockIndex={[blockIndex]}
            setBlocks={setBlocks}
          />
          </div>
        ))}
      </ReactSortable>
  );
}
function Container({ block, blockIndex, setBlocks }) {
  return (
    <>
      <ReactSortable
        key={block.id}
        list={block.children}
        setList={(currentList) => {
          setBlocks((sourceList) => {
            const tempList = [...sourceList];
            const _blockIndex = [...blockIndex];
            const lastIndex = _blockIndex.pop();
            const lastArr = _blockIndex.reduce(
              (arr, i) => arr[i]["children"],
              tempList
            );
            console.log(lastIndex);
            lastArr[lastIndex]["children"] = currentList;
            return tempList;
          });
        }}
        {...sortableOptions}
      >
        {block.children &&
          block.children.map((childBlock, index) => {
            return (
              <BlockWrapper
                key={childBlock.id}
                block={childBlock}
                blockIndex={[...blockIndex, index]}
                setBlocks={setBlocks}
              />
            );
          })}
      </ReactSortable>
    </>
  );
}
function BlockWrapper({ block, blockIndex, setBlocks }) {
  // console.log(block);
  if (!block) return null;
  if (block.type === "container") {
    return (
      <StyledBlockWrapper className="block">
          {block.content}
        <Container
          block={block}
          setBlocks={setBlocks}
          blockIndex={blockIndex}
        />
      </StyledBlockWrapper>
    );
  } else {
    return (
      <StyledBlockWrapper className="block">
        {block.content}
      </StyledBlockWrapper>
    );
  }
}
